import React from 'react';
import image from '../Image/R.jpg';

const ChatItem = ({ name, message, time, count }) => {
  return(
<button className='flex justify-between h-16 mb-4 focus:bg-red-500'>
  <div className='flex ml-6'>
    <img className="w-12 h-12 rounded-lg border-white" src={image} alt="Profile" />
    <div className='flex flex-col ml-6'>
      <h1 className='text-left font-bold'>{name}</h1>
      <div className='flex flex-row gap-6 text-xs text-gray-400'>
        <label>{message}</label>
        <label>{time}</label>
      </div> 
    </div>
  </div>
  <div className='mt-3 mr-10 w-6 h-6 text-white bg-red-500 rounded-full'>{count}</div>
</button>
  )
}
export default ChatItem
