import React, { useState } from 'react'
import image from '../Image/R.jpg'; // adjust the path as needed
import user from '../Image/User.png';
import message from '../Image/message-white.png';
import date from '../Image/date-white.png';
import setting from '../Image/setting-white.png';

const LeftNavbar = () => {
  const [activeButton, setActiveButton] = useState(null);

  const handleButtonClick = (buttonName) => {
    setActiveButton(buttonName);
  }

  return (
    <div className="bg-black flex flex-col items-center w-24 h-screen">
      <img className="w-16 h-16 rounded-2xl mt-20 mb-10 w-14 h-14 border-white" src={image} alt="Profile" />
      <button
        className={`btn btn-square w-6 h-4 mb-8 focus:bg-white ${activeButton === 'user' ? 'bg-orange' : ''}`}
        onClick={() => handleButtonClick('user')}
      >
        <img className='w-6 h-4' src={user} alt='User' />
      </button>
      <button
        className={`btn btn-square w-6 h-4 mb-10 ${activeButton === 'message' ? 'bg-orange' : ''}`}
        onClick={() => handleButtonClick('message')}
      >
        <img className='w-6 h-4' src={message} alt='Message' />
      </button>
      <button
        className={`btn btn-square w-6 h-4 mb-10 ${activeButton === 'date' ? 'bg-orange' : ''}`}
        onClick={() => handleButtonClick('date')}
      >
        <img className='w-6 h-4' src={date} alt='Date' />
      </button>
      <button
        className={`btn btn-square w-6 h-4 mb-10 ${activeButton === 'setting' ? 'bg-orange' : ''}`}
        onClick={() => handleButtonClick('setting')}
      >
        <img className='w-6 h-4' src={setting} alt='Setting' />
      </button>
    </div>
  )
}

export default LeftNavbar
