import React from 'react';
import image from '../Image/R.jpg';

const ChatItem = ({ name, message, time, count }) => {
  return(
  <button className='flex ml-6'>
    <img className="w-10 h-10 rounded-xl border-white" src={image} alt="Profile" />
    <div className='flex flex-col ml-6'>
      <h1 className='text-left'>{name}</h1>
      <div className='flex flex-row gap-6 text-xs text-gray-400'>
      <label>{message}</label>
      <label>{time}</label>
      </div> 
    </div>
    <div className='mt-3 ml-12 w-6 h-6 text-white bg-red-600 rounded-full'>{count}</div>
  </button>
  )
}
export default ChatItem
